INSERT INTO BookKeywords (BookID, KeywordID) VALUES (@last_book_id, @last_keyword_id);INSERT INTO Book (ISBN, Title, Publisher, Number_of_pages, Summary, Image, language, OperatorID, SchoolID) VALUES ("9781498290371", "Humor Us",  "Wipf and Stock Publishers", 194, "This book addresses the fact that Americans tend to live under a considerable amount of stress, tension, and anxiety, and suggests that humor can be helpful in alleviating their distress. It posits that humor is a useful placebo in this regard; cites studies that show that humor moderates life stress; considers the relationship of religion and humor, especially as means to alleviate anxiety; proposes that Jesus had a sense of humor; suggests that his parable of the Laborers in the Vineyard has humorous implications for the relief of occupational stress; explores the relationship of gossip and humor; and suggests that Jesus and his disciples were a joking community. It concludes that Jesus viewed the kingdom of God as a worry-free existence.", "http://books.google.com/books/content?id=6TRQDQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api", "en", 3, 2);
