INSERT INTO BookKeywords (BookID, KeywordID) VALUES (@last_book_id, @last_keyword_id);INSERT INTO Book (ISBN, Title, Publisher, Number_of_pages, Summary, Image, language, OperatorID, SchoolID) VALUES ("9789027264626", "The Dynamics of Interactional Humor",  "John Benjamins Publishing Company", 324, "This book deals with the construction of diverse forms of humor in everyday oral, written, and mediatized interactions. It sheds light on the differences and, most importantly, the similarities in the production of interactional humor in face-to-face and various technology-mediated forms of communication, including scripted and non-scripted situations. The chapters analyze humor-related issues in such genres as spontaneous conversations, broadcast dialogues, storytelling, media blogs, bilingual conversations, stand-up comedy, TV documentaries, drama series, family sitcoms, Facebook posts, and internet memes. The individual authors trace how speakers collaboratively circulate, reconstruct, and (re)frame either personal or public accounts of reality, aiming \u2013among other things\u2013 to produce and/or reproduce humor. Rather than being \u201cfinished\u201d products with a \u201csingle\u201d interpretation, humorous texts are thus approached as dynamic communicative events that give rise to diverse interpretations and meanings. The book draws on a variety of up-to-date approaches and methodologies, and will appeal to scholars in discourse analysis, conversation analysis, interactional sociolinguistics, pragmatics, ethnography of communication, and social semiotics.", "http://books.google.com/books/content?id=-qZFDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api", "en", 7, 6);
