INSERT INTO BookKeywords (BookID, KeywordID) VALUES (@last_book_id, @last_keyword_id);INSERT INTO Book (ISBN, Title, Publisher, Number_of_pages, Summary, Image, language, OperatorID, SchoolID) VALUES ("9781434458841", "Ellis Parker Butler on the Fine Art of Writing Humor",  "Wildside Press LLC", 48, "Ellis Parker Butler (1869-1937) was the author of more than 2,000 stories and essays, as well as 30 books. He remains most famous for his short story \"Pigs is Pigs,\" in which a bureaucratic stationmaster insists on levying the livestock rate for a shipment of two pet guinea pigs, which soon start proliferating geometrically. This collection reveals his thoughts on writing humor, including \"Ten Rules for Humor\" and \"How I Sell My Stories.\"", "http://books.google.com/books/content?id=iVY4Ut6_JjEC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api", "en", 5, 4);
