INSERT INTO BookKeywords (BookID, KeywordID) VALUES (@last_book_id, @last_keyword_id);INSERT INTO Book (ISBN, Title, Publisher, Number_of_pages, Summary, Image, language, OperatorID, SchoolID) VALUES ("9781642507614", "The Best New True Crime Stories: Partners in Crime",  "Mango Media", 235, "A true crime anthology exploring the dangerous side of romance\u2014with couples who bonded over murder, mayhem and more. What brings criminal couples together? And what drives them apart? This volume of The Best New True Crime Stories attempts to answer these questions with a deep dive into true tales of lawless love. Everyone\u2019s heard of Bonnie and Clyde, but the annals of crime history are full of dysfunctional duos whose deadly escapades are equally enthralling. Featuring contributions from an international list of award-winning crime writers, journalists, and experts in the dark crimes field, The Best New True Crime Stories: Partners in Crime is a must-read for any true crime afficionado. Because when love goes wrong, there\u2019s never a dull moment.", "http://books.google.com/books/content?id=bH03EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api", "en", 2, 1);
