INSERT INTO BookKeywords (BookID, KeywordID) VALUES (@last_book_id, @last_keyword_id);INSERT INTO Book (ISBN, Title, Publisher, Number_of_pages, Summary, Image, language, OperatorID, SchoolID) VALUES ("9798668604159", "Joe The Cannibal",  "Independently Published", 284, "THE HUMAN HAMBURGER STANDJoe Metheny was a serial killer active in Baltimore, Maryland in the 1990s. He claimed that he had killed up to ten victims. However, he was convicted of only two cases of murder and one case of kidnapping. He mostly preyed on young white prostitutes who like himself, struggled with drug addiction. The most bizarre thing about him was the fact that he made burgers out of the flesh of some of his victims. He then sold these burgers to unsuspecting customers at a barbecue stand he opened by the side of the road. Because of this, he was given the nickname \"The Cannibal\".This is his story.", "http://books.google.com/books/content?id=Q0PAzQEACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api", "en", 8, 7);
