INSERT INTO BookKeywords (BookID, KeywordID) VALUES (@last_book_id, @last_keyword_id);INSERT INTO Book (ISBN, Title, Publisher, Number_of_pages, Summary, Image, language, OperatorID, SchoolID) VALUES ("9780698159259", "Unlucky Number",  "Penguin", 304, "The true crime story of murdered Florida lottery winner Abraham Shakespeare. Poor man. Rich man. Dead man. It sounded like a fairy tale: A homeless man named Abraham Shakespeare spent his last dollars on a Florida State lottery ticket\u2014and miraculously won $31 million. Unprepared for his new found fortune, Abraham hired Dorice \u201cDee Dee\u201d Moore to help manage his winnings and field the numerous requests for loans and assistance that he received. But somehow, Dee Dee was the only one benefiting. When Abraham quietly disappeared from his home in Florida, friends and family grew suspicious\u2014though he could not read or write, his only form of contact was through odd letters and texts. But it wasn\u2019t until investigators began to question Dee Dee about her role in Abraham\u2019s finances that a complicated web of lies\u2014and the desperate lengths to which one woman would go to cover it up\u2014was exposed\u2026", "http://books.google.com/books/content?id=p1j2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api", "en", 4, 3);
