INSERT INTO BookKeywords (BookID, KeywordID) VALUES (@last_book_id, @last_keyword_id);INSERT INTO Book (ISBN, Title, Publisher, Number_of_pages, Summary, Image, language, OperatorID, SchoolID) VALUES ("9781506717647", "Disney/PIXAR Monsters Inc. and Monsters University: The Story of the Movies in Comics",  "Dark Horse Comics", 106, "Join Mike Wazowski, Sully, and all their coworkers and classmates on the hilarious and occasionally scary journey that brings DisneyPixar's Monsters Inc. and Monsters University from the screen to your fingertips! Ever since Mike Wazowski was a little monster, he dreamed of becoming a Scarer--and he knows better than anyone that the best Scarers come from Monsters University. But during his first semester at MU, Mike's plans are derailed when he crosses paths with hotshot, James P. Sullivan, \"Sulley\", a natural-born Scarer. The pair's out of control competitive spirit gets them both kicked out of the University's elite Scare Program. With their dreams temporarily dashed, they realize they will have to work together, along with an odd bunch of misfit monsters, if they ever hope to make things right. Top scare-team at Monsters, Inc., Mike and Sully are in for a wild ride when a little girl named Boo wanders into the scream-processing plant. Trying to protect themselves and Boo from decontamination, the two monsters learn that being scared silly with laughter is much more powerful than screams.", "http://books.google.com/books/content?id=peDtDwAAQBAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api", "en", 3, 2);
